document.addEventListener("DOMContentLoaded",function(){Array.from(document.querySelectorAll('[event-size="wrapper"]')).forEach(e=>{const t=e.querySelector('[event-size="guest"]'),n=e.querySelector('[event-size="area"]');t&&n&&n.addEventListener("input",function(){n.value.trim()!==""?t.removeAttribute("required"):t.setAttribute("required","true")})})});
