document.addEventListener("DOMContentLoaded",function(){const e=t=>{const n=Array.from(document.querySelectorAll(`[${t}]`));const i=t=>{if(t<n.length-1){const e=n[t+1];e.classList.contains("start-hide")&&(e.classList.remove("start-hide"),e.style.display="flex")}};n.forEach((e,t)=>{const n=e.querySelector('input[type="file"]');n&&n.addEventListener("change",function(){n.value&&i(t)})})};e("upload-private"),e("upload-pro")});
