function updatePriceIndicators(){document.querySelectorAll("[product-price-level][product-price-range]").forEach((e=>{const t=parseInt(e.getAttribute("product-price-level")),r=parseInt(e.getAttribute("product-price-range"));e.innerHTML="";for(let c=1;c<=r;c++){const r=document.createElement("div");r.setAttribute("product-price","indicator"),r.classList.add("product-card_price"),c<=t&&r.classList.add("is-active"),r.textContent=e.textContent.trim()||"",e.appendChild(r)}}))}function observeLazyLoad(){new MutationObserver((e=>{e.forEach((e=>{e.addedNodes.forEach((e=>{e.nodeType===Node.ELEMENT_NODE&&e.querySelectorAll&&e.querySelectorAll("[product-price-level][product-price-range]").length>0&&updatePriceIndicators()}))}))})).observe(document.body,{childList:!0,subtree:!0})}document.addEventListener("DOMContentLoaded",(()=>{updatePriceIndicators(),observeLazyLoad()}));
