function updatePriceIndicators(){document.querySelectorAll('[product-price-level][product-price-range]').forEach(e=>{const t=parseInt(e.getAttribute("product-price-level")),n=parseInt(e.getAttribute("product-price-range"));e.innerHTML="";const r=(navigator.language||navigator.userLanguage).includes("en-GB")||navigator.language.includes("en")?"£":navigator.language.includes("de-CH")||navigator.language.includes("fr-CH")?"CHF":"€";for(let c=1;c<=n;c++){const n=document.createElement("div");n.setAttribute("product-price","indicator"),n.classList.add("product-card_price"),c<=t&&n.classList.add("is-active"),n.textContent=r,e.appendChild(n)}})}window.addEventListener("DOMContentLoaded",updatePriceIndicators);
